import{AssertNotNull}from"/static/engine/assertions/AssertNotNull.js";import{BodyPart}from"/static/engine/chicken_stuff/BodyPart.js";import{Random}from"/static/engine/code_tools/misc/Random.js";export class Killed{constructor(t){AssertNotNull(t,"argument c in "+this.constructor.name+".js should not be null"),this.c=t;const o="/static/assets/sprites/dead_chicken_32x32.png";this.splatteredBody=[new BodyPart(Random.direction(t,10),o,{x:1,y:0}),new BodyPart(Random.direction(t,10),o,{x:2,y:0}),new BodyPart(Random.direction(t,10),o,{x:2,y:1}),new BodyPart(Random.direction(t,10),o,{x:1,y:1}),new BodyPart(Random.direction(t,10),o,{x:1,y:1})]}draw(t,o){for(const s of this.splatteredBody)s.draw(t,o)}}