import{Position}from"/static/engine/position/Position.js";import{D}from"/static/game/world/D.js";export class Random{static integerBetween(t,e){return Math.floor(Math.random()*(e-t+1)+t)}static chance(){return 1==this.integerBetween(0,1)}static percentageChance(t){return 1==this.integerBetween(0,t)}static floatBetween(t,e){const n=Math.random()*(e-t)+t;return parseFloat(n.toFixed(2))}static color(){return`hsl(${360*Math.random()}, 100%, 50%)`}static choice(t){return t[Math.floor(Math.random()*t.length)]}static direction(t,e=200){const n=Random.integerBetween(t.x-e,t.x+e),i=Random.integerBetween(t.y-e,t.y+e);return new Position(n,i)}static positionWithin(t){return this.position(t.x,t.x+t.width,t.y,t.y+t.height)}static position(t,e,n,i){const o=Random.integerBetween(t,e),r=Random.integerBetween(n,i);return new Position(o,r,1,1)}static positions(t,e,n,i,o){const r=[];for(let a=0;a<o;a++)r.push(Random.position(t,e,n,i));return r}static uuid(){return crypto.randomUUID().toString()}}