export const Move=t=>({awayFrom:(a,o=1)=>{const c=Math.atan2(t.y-a.y,t.x-a.x);t.x+=Math.cos(c)*t.velocityFactor*o,t.y+=Math.sin(c)*t.velocityFactor*o},towards:(a,o=1)=>{const c=Math.atan2(a.y-t.y,a.x-t.x);t.x+=Math.cos(c)*t.velocityFactor*o,t.y+=Math.sin(c)*t.velocityFactor*o},to:(a,o,c=1)=>{const y=Math.atan2(a.y-t.y,a.x-t.x)+o*Math.PI/180;t.x+=Math.cos(y)*t.velocityFactor*c,t.y+=Math.sin(y)*t.velocityFactor*c}});