import{ForcePush}from"/static/engine/core/physics/ForcePush.js";export const Push=o=>({awayFrom:(t,c=1)=>{const a=Math.atan2(o.y-t.y,o.x-t.x);o.velocity.x+=Math.cos(a)*o.velocityFactor*c,o.velocity.y+=Math.sin(a)*o.velocityFactor*c},towards:(t,c=1)=>{const a=Math.atan2(t.y-o.y,t.x-o.x);o.velocity.x+=Math.cos(a)*o.velocityFactor*c,o.velocity.y+=Math.sin(a)*o.velocityFactor*c},roughlyTowards:(t,c=1)=>{ForcePush(o).roughlyTowards(t,c)}});