import{AssertNotNull}from"/static/engine/assertions/AssertNotNull.js";import{SimplexNoise}from"/static/engine/graphics/SimplexNoise.js";import{Positions}from"/static/engine/position/Positions.js";export class Noise{constructor(s,i=10){AssertNotNull(s,"argument position in "+this.constructor.name+".js should not be null"),AssertNotNull(i,"argument size in "+this.constructor.name+".js should not be null"),this.position=s,this.size=i,this.simplexNoise=new SimplexNoise(.001),this.time=0,this.positionAndNoiseValue=[],this.positionAndNoiseValue=Positions.grid(s,i).map(s=>({position:s,noise:this.simplexNoise.noise(s,this.time)}))}draw(s,i){for(const{position:i,noise:o}of this.positionAndNoiseValue)o>=.5?s.blueRectangle(i):s.green(i)}}