import{Html}from"/static/engine/graphics/ui/html/Html.js";const bar=Html.div("lower-center-ui progress-container",[Html.div("progress-bar")]);export class HtmlProgressBar{static progress=0;static create(){Html.show(bar)}static change(t){this.progress+=t;let s=document.getElementsByClassName("progress-bar")[0];s.style.width=this.progress+"%",s.textContent=this.progress+"%"}static remove(){Html.hide(bar)}}