import{AssertNotNull}from"/static/engine/assertions/AssertNotNull.js";import{Loop}from"/static/engine/core/Loop.js";export class Hp{constructor(t,s=100,o=100,e=()=>{}){AssertNotNull(t,"argument object in "+this.constructor.name+".js should not be null"),AssertNotNull(s,"argument currentHp in "+this.constructor.name+".js should not be null"),AssertNotNull(o,"argument maxHp in "+this.constructor.name+".js should not be null"),AssertNotNull(e,"argument killed in "+this.constructor.name+".js should not be null"),this.object=t,this.currentHp=s,this.maxHp=o,this.killed=e}get dead(){return this.currentHp<=0}update(){this.dead&&(this.killed(),this.removeFromLoop())}draw(t,s){t.hpBar(this.object.position.offset(100,-100),this.currentHp,this.maxHp)}}