import{List}from"/static/engine/code_tools/misc/List.js";import{Html}from"/static/engine/graphics/ui/html/Html.js";export class QuestList{static add(t){const s=Html.p(t);return Html.addToScreen(Html.div("ui left",[s])),{completed:()=>s.classList.add("crossed"),text:t=>s.innerHTML=t}}static clear(){Html.removeChildElementsInId("quest_list")}}