import{Palette}from"/static/engine/core/Palette.js";import{Position}from"/static/engine/position/Position.js";export class GuiPosition{static bottomMiddle(t=0,e=0,i=1,o=1){const n=Palette.width/2+t,s=Palette.height-50+e;return new Position(n,s,i,o)}static topMiddle(){const t=Palette.width/2;return new Position(t,50)}static leftMiddle(){const t=Palette.height/2;return new Position(50,t)}static rightMiddle(){const t=Palette.width-50,e=Palette.height/2;return new Position(t,e)}static middle(t=0,e=0){const i=(Palette.width+t)/2,o=(Palette.height+e)/2;return new Position(i,o)}}