import{Position}from"/static/engine/position/Position.js";export class Microphone{constructor(){if("webkitSpeechRecognition"in window){const t=new webkitSpeechRecognition;t.lang="en-US",t.continuous=!0,t.interimResults=!0,t.start(),t.onresult=t=>{let e="";for(let o=t.resultIndex;o<t.results.length;o++)e+=t.results[o][0].transcript;this.transcript=e},t.onerror=function(t){console.error("Speech recognition error",t.error)}}else alert("Speech recognition is not supported in your browser.")}update(){}draw(t,e){t.text(new Position(0,0),this.transcript)}}