import{AssertNotNull}from"/static/engine/assertions/AssertNotNull.js";import{Position}from"/static/engine/position/Position.js";export class Shake{constructor(t){AssertNotNull(t,"argument sprite in "+this.constructor.name+".js should not be null"),this.sprite=t,this.sprite=t,this.time=0,this.intensity=0,this.offset={x:0,y:0}}start(t=200,i=2){this.time=t,this.intensity=i}update(){this.time>0?(this.time-=16,this.offset.x=(Math.random()-.5)*this.intensity*2,this.offset.y=(Math.random()-.5)*this.intensity*2):(this.offset.x=0,this.offset.y=0)}getPosition(){return{x:this.sprite.position.x+this.offset.x,y:this.sprite.position.y+this.offset.y,width:this.sprite.position.width,height:this.sprite.position.height}}}